#ifndef __ED_USER_H
#define __ED_USER_H

#ifdef __cplusplus

/*==============================================================================
	This class is used to store EnDec runtime messages into a CString, so
	it can be retrieved later.
==============================================================================*/
class TUserInterface {
	public:
		/*--------------------------------------------------------------------------
			CONSTRUCTOR
		--------------------------------------------------------------------------*/
		TUserInterface ();

		/*--------------------------------------------------------------------------
			DESTRUCTOR
		--------------------------------------------------------------------------*/
		virtual ~TUserInterface ();

		/*--------------------------------------------------------------------------
			Add message
		--------------------------------------------------------------------------*/
		void AddMessage (const char* messageText);

		char* text;
};

#define ED_SIGNAL_ERROR(errtext) userInterface.AddMessage (errtext);
#endif

#endif //__ED_USER_H